#include <stdio.h>

void tambah(int n) {
    static int total = 0;
    int temp = 0;

    total += n;
    temp += n;

    printf("%d %d
", total, temp);
}

int main() {
    tambah(2);
    tambah(3);
    tambah(5);
    return 0;
}

