#include <stdio.h>

int hitungLuas(int panjang, int lebar) {
    return panjang * lebar;
}

void tampilkanPesan() {
    printf("Menghitung luas persegi panjang...\n");
}

int main() {
    tampilkanPesan();
    int hasil = hitungLuas(5, 3);
    printf("Luas: %d\n", hasil);
    return 0;
}

